// src/components/WorkoutComponent.jsx
import React, { useState } from 'react';
import WorkoutForm from './WorkoutForm';
import ExerciseForm from './ExerciseForm';

const WorkoutComponent = () => {
  const [userId, setUserId] = useState('');
  const [workoutId, setWorkoutId] = useState(null);
  const [exercises, setExercises] = useState([]);
  const [currentExerciseIndex, setCurrentExerciseIndex] = useState(0);

  const handleBeginWorkout = (userId, workoutId, exercises) => {
    setUserId(userId);
    setWorkoutId(workoutId);
    setExercises(exercises);
    setCurrentExerciseIndex(0);
  };

  const handleExerciseSubmit = () => {
    setCurrentExerciseIndex((prevIndex) => prevIndex + 1);
  };

  const handleBack = () => {
    setCurrentExerciseIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleEndWorkout = async () => {
    try {
      await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/end`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      alert('Workout completed!');
    } catch (error) {
      console.error('Error ending workout:', error);
    }
  };

  if (exercises.length === 0) {
    return <WorkoutForm onBegin={handleBeginWorkout} />;
  }

  return (
    <div>
      {exercises[currentExerciseIndex] ? (
        <ExerciseForm
          key={exercises[currentExerciseIndex].exercise_id}
          workoutId={workoutId}
          exercise={exercises[currentExerciseIndex]}
          onSubmit={handleExerciseSubmit}
          onBack={handleBack}
        />
      ) : (
        <button onClick={handleEndWorkout}>End Workout</button>
      )}
    </div>
  );
};

export default WorkoutComponent;
