import React, { useState, useEffect } from 'react';

const ExerciseForm = ({ workoutId, exercise, onSubmit, onBack }) => {
  const [currentSet, setCurrentSet] = useState(1);
  const [reps, setReps] = useState('');
  const [weight, setWeight] = useState('');
  const [restTime, setRestTime] = useState('');
  const [completedExerciseId, setCompletedExerciseId] = useState(null);
  const [sets, setSets] = useState([]);
  const [currentSetId, setCurrentSetId] = useState(null);

  useEffect(() => {
    if (exercise) {
      const initializeExercise = async () => {
        try {
          const startTime = new Date().toISOString();
          const response = await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/exercises`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ goal_weight: exercise.goal_weight, start_time: startTime })
          });
          const data = await response.json();
          console.log("RAW DATA: ", data)
          setCompletedExerciseId(data.completedExerciseId);

          // Create the first set automatically
          const setResponse = await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/exercises/${data.completedExerciseId}/sets`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ reps: 0, weight: 0, rest_time: 0 })
          });
          const setData = await setResponse.json();
          console.log("SETDATA: ", setData);
          if (setData && setData.set && setData.set.completed_set_id) {
            setCurrentSetId(setData.set.completed_set_id);
          } else {
            console.error('Set ID not found in response:', setData);
          }
        } catch (error) {
          console.error('Error initializing exercise:', error);
        }
      };

      initializeExercise();
    }
  }, [workoutId, exercise]);

  const handleSetChange = (setter, value) => {
    setter(value);
    if (reps && weight && (exercise.set_type_id !== 4 || restTime)) {
      saveSet(value);
    }
  };

  const saveSet = async (value) => {
    try {
      await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/exercises/${completedExerciseId}/sets/${currentSetId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ reps, weight, rest_time: restTime })
      });

      // Prepare for the next set or complete the exercise
      setReps('');
      setWeight('');
      setRestTime('');

      if (exercise.set_type_id === 1 && currentSet < 3) {
        const newSetResponse = await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/exercises/${completedExerciseId}/sets`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ reps: 0, weight: 0, rest_time: 0 })
        });
        const newSetData = await newSetResponse.json();
        console.log("NEW SETDATA: ", newSetData);
        if (newSetData && newSetData.set && newSetData.set.completed_set_id) {
          setCurrentSetId(newSetData.set.completed_set_id);
          setCurrentSet(currentSet + 1);
        } else {
          console.error('New set ID not found in response:', newSetData);
        }
      } else {
        handleSubmit();
      }
    } catch (error) {
      console.error('Error saving set:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      const endTime = new Date().toISOString();
      await fetch(`https://bwg36wqc6b.execute-api.us-east-1.amazonaws.com/dev/workouts/${workoutId}/exercises/${completedExerciseId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ goal_weight: weight, end_time: endTime })
      });
      onSubmit();
    } catch (error) {
      console.error('Error submitting exercise:', error);
    }
  };

  if (!exercise) {
    return <div>Loading exercise...</div>;
  }

  return (
    <div>
      <h2>{exercise.name}</h2>
      <p>{exercise.description}</p>
      {exercise.set_type_id === 1 ? (
        <>
          <p>Set {currentSet}</p>
          <p>{exercise.min_reps} - {exercise.max_reps} reps</p>
          <p>Recommended weight: {exercise.goal_weight}</p>
        </>
      ) : (
        <p>Burn Set</p>
      )}
      <input
        type="number"
        value={reps}
        onChange={(e) => handleSetChange(setReps, e.target.value)}
        placeholder="Reps"
      />
      <input
        type="number"
        value={weight}
        onChange={(e) => handleSetChange(setWeight, e.target.value)}
        placeholder="Weight"
      />
      {exercise.set_type_id === 1 && (
        <input
          type="number"
          value={restTime}
          onChange={(e) => handleSetChange(setRestTime, e.target.value)}
          placeholder="Rest Time"
        />
      )}
      <button onClick={onBack}>Back</button>
      <button onClick={handleSubmit}>Next</button>

      <div>
        <h3>Completed Sets</h3>
        {sets.map((set, index) => (
          <p key={index}>Set {set.setNumber}: {set.reps} reps, {set.weight} lbs, {set.rest_time} sec rest</p>
        ))}
      </div>
    </div>
  );
};

export default ExerciseForm;
