// src/components/ExerciseForm.jsx
import React, { useState } from 'react';

const ExerciseForm = ({ userId, workoutId, exercises }) => {
  const [currentExerciseIndex, setCurrentExerciseIndex] = useState(0);
  const [sets, setSets] = useState('');
  const [reps, setReps] = useState('');
  const [weight, setWeight] = useState('');
  const [restTime, setRestTime] = useState('');

  const currentExercise = exercises[currentExerciseIndex];

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
      exerciseId: currentExercise.exercise_id,
      userId,
      sets,
      reps,
      weight,
      restTime,
    };

    try {
      await fetch(`https://your-api-gateway-endpoint/workouts/${workoutId}/exercises`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      // Move to next exercise after submission
      if (currentExerciseIndex < exercises.length - 1) {
        setCurrentExerciseIndex(currentExerciseIndex + 1);
        setSets('');
        setReps('');
        setWeight('');
        setRestTime('');
      }
    } catch (error) {
      console.error('Error submitting exercise data:', error);
    }
  };

  const handleSkip = () => {
    if (currentExerciseIndex < exercises.length - 1) {
      setCurrentExerciseIndex(currentExerciseIndex + 1);
      setSets('');
      setReps('');
      setWeight('');
      setRestTime('');
    }
  };

  const handleBack = () => {
    if (currentExerciseIndex > 0) {
      setCurrentExerciseIndex(currentExerciseIndex - 1);
      setSets('');
      setReps('');
      setWeight('');
      setRestTime('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Exercise:</label>
        <input type="text" value={currentExercise.exercise_id || ''} readOnly />
      </div>
      <div>
        <label>Sets:</label>
        <input
          type="number"
          value={sets}
          onChange={(e) => setSets(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Reps:</label>
        <input
          type="number"
          value={reps}
          onChange={(e) => setReps(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Weight:</label>
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Rest Time:</label>
        <input
          type="number"
          value={restTime}
          onChange={(e) => setRestTime(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit</button>
      <button type="button" onClick={handleSkip}>Skip</button>
      <button type="button" onClick={handleBack}>Back</button>
    </form>
  );
};

export default ExerciseForm;
